def fetch_pet_info(pm_name, soup):
    if soup is None:
        url = (f"https://wiki.52poke.com/wiki/%E5%AE%9D%E5%8F%AF%E6%A2%A6%E5%88%97%E8%A1%A8%EF%BC%88%E6%8C%89%E5%85"
               f"%A8%E5%9B%BD%E5%9B%BE%E9%89%B4%E7%BC%96%E5%8F%B7%EF%BC%89")  # 替换为实际的宠物页面URL
        response = requests.get(url)
        soup = BeautifulSoup(response.text, "html.parser")
    trs = soup.find_all("tr")

    for tr in trs:
        tds = tr.find_all("td")
        if len(tds) > 7 and tds[3].text.strip() == pm_name:
            pm_id = tds[0].text.strip()
            pm_type1 = tds[6].text.strip()
            pm_type2 = tds[7].text.strip() if "属性" not in tds[7].text.strip() else None
            pm_id = int(pm_id[1:])
            print("{}_{}_{}_{}".format(pm_id, pm_name, pm_type1, pm_type2))
            insert_pm_into_db(pm_id, pm_name, pm_type1, pm_type2)
            return soup
    return soup


def fetch_move_info(move_name, soup):
    if soup is None:
        url = f"https://wiki.52poke.com/wiki/%E6%8B%9B%E5%BC%8F%E5%88%97%E8%A1%A8"  # 替换为实际的招式页面URL
        response = requests.get(url)
        soup = BeautifulSoup(response.text, "html.parser")
    trs = soup.find_all("tr")

    for tr in trs:
        tds = tr.find_all("td")
        if len(tds) > 9 and (tds[1].text.strip() == move_name or tds[1].text.strip() == (move_name + '*')):
            type = tds[4].text.strip()
            power = tds[6].text.strip()
            accuracy = tds[7].text.strip()
            if accuracy == '—':
                accuracy = 200
            print("{}_{}_{}_{}".format(move_name, type, power, accuracy))
            insert_move_into_db(move_name, type, power, accuracy)
            return soup
    return soup


