def query_pm(pm_name, pm_soup, unknown_pm_id=0):
    query = "SELECT ID FROM tb_pm_info WHERE PM_Name = %s AND PM_Form is NULL"
    res = query_db(query, (pm_name,))
    if res:
        return res[0], pm_soup
    else:
        soup = fetch_pet_info(pm_name, pm_soup)
        res = query_db(query, (pm_name,))
        if res:
            return res[0], soup
        else:
            return unknown_pm_id, soup


def query_move(move_name, move_soup, unknown_move_id=0):
    query = "SELECT Move_ID FROM tb_move_info WHERE Move_Name = %s"
    res = query_db(query, (move_name,))
    if res:
        return res[0], move_soup
    else:
        soup = fetch_move_info(move_name, move_soup)
        res = query_db(query, (move_name,))
        if res:
            return res[0], soup
        else:
            return unknown_move_id, soup


